package com.mbc.mjy_board.book;

public class BookDTO {
	int booknumber;
	String bookname, bookwriter, bookcompany;
	int bookprice;
	String bookdate, bookcontent, bookimage;
	int bookreadcnt;
	int groups, step, indent;
	public BookDTO() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int getBooknumber() {
		return booknumber;
	}
	public void setBooknumber(int booknumber) {
		this.booknumber = booknumber;
	}
	public String getBookname() {
		return bookname;
	}
	public void setBookname(String bookname) {
		this.bookname = bookname;
	}
	public String getBookwriter() {
		return bookwriter;
	}
	public void setBookwriter(String bookwriter) {
		this.bookwriter = bookwriter;
	}
	public String getBookcompany() {
		return bookcompany;
	}
	public void setBookcompany(String bookcompany) {
		this.bookcompany = bookcompany;
	}
	public int getBookprice() {
		return bookprice;
	}
	public void setBookprice(int bookprice) {
		this.bookprice = bookprice;
	}
	public String getBookdate() {
		return bookdate;
	}
	public void setBookdate(String bookdate) {
		this.bookdate = bookdate;
	}
	public String getBookcontent() {
		return bookcontent;
	}
	public void setBookcontent(String bookcontent) {
		this.bookcontent = bookcontent;
	}
	public String getBookimage() {
		return bookimage;
	}
	public void setBookimage(String bookimage) {
		this.bookimage = bookimage;
	}
	public int getBookreadcnt() {
		return bookreadcnt;
	}
	public void setBookreadcnt(int bookreadcnt) {
		this.bookreadcnt = bookreadcnt;
	}
	public int getGroups() {
		return groups;
	}
	public void setGroups(int groups) {
		this.groups = groups;
	}
	public int getStep() {
		return step;
	}
	public void setStep(int step) {
		this.step = step;
	}
	public int getIndent() {
		return indent;
	}
	public void setIndent(int indent) {
		this.indent = indent;
	}
	
	
	
	
	
}
